import React from 'react';
import { FaStar, FaStarHalfAlt } from 'react-icons/fa';
import {AiOutlineStar } from 'react-icons/ai';


const Rating = ({ value, text }) => {
  return (
    <div className="rating">
    {/* <span>
        <i className={value >= 1 ? "fas fa-star" : value >= 0.5 ? "fas fa-star-half-alt" : "fas fa-start"}></i>
    </span> */}
   <span>
         {value >= 1 ? (
          <FaStar />
         ) : value >= 0.5 ? (
           <FaStarHalfAlt />
         ) : (
           <AiOutlineStar />
         )}
       </span>
       <span>
         {value >= 2 ? (
           <FaStar />
         ) : value >= 1.5 ? (
           <FaStarHalfAlt />
         ) : (
           <FaStart />
         )}
       </span>
       <span>
         {value >= 3 ? (
           <FaStar />
         ) : value >= 2.5 ? (
           <FaStarHalfAlt />
         ) : (
           <FaStart />
         )}
       </span>
       <span>
         {value >= 4 ? (
           <FaStar />
         ) : value >= 3.5 ? (
           <FaStarHalfAlt />
         ) : (
           <FaStart />
         )}
       </span>
       <span>
         {value >= 5 ? (
           <FaStar />
         ) : value >= 4.5 ? (
           <FaStarHalfAlt />
         ) : (
           <FaStart />
         )}
       </span>

    </div>
  );

 }

export default Rating




